combined_order_summary <- bind_rows("FY19" = fy19_order_summary,
                                    "FY20" = fy20_order_summary,
                                    .id = "fiscal_year")

combined_order_summary <- combined_order_summary %>%
  mutate(Brand = if_else(str_detect(`Order Type`, "CLAIM"), "CLAIM",
                         if_else(str_detect(`Order Type`, "PART"), "PARTS ORDER",
                                 if_else(str_detect(`Order Type`, "GB"), "BUTLER",
                                         if_else(str_detect(`Order Type`, "VP"), "VP",
                                                 if_else(str_detect(`Order Type`, "CSS/CONV"), "CSS",
                                                         if_else(str_detect(`Order Type`, "CSS/HS"), "HEAVY STRUCTURES",
                                                                 if_else(str_sub(`Order Type`, 1,4) == "ROOF", "ROOF", "Other"))))))),
         eck_month = floor_date(ymd(`ECK Act Comp`), "month"),
         tons = if_else(`Order Status` == "EN", `Planned Tons`, `Eng Mfst Tons`)) %>%
  left_join(eng_complexity_lookup, by = c("proj_num" = "proj_num")) %>%
  clean_names()